"use client"

import { useState, useEffect } from "react"

export default function Captcha({ onVerify, resetTrigger = 0 }) {
  const [captchaText, setCaptchaText] = useState("")
  const [captchaId, setCaptchaId] = useState("")
  const [answer, setAnswer] = useState("")
  const [isVerified, setIsVerified] = useState(false)
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    generateCaptcha()
    setIsVerified(false)
    setAnswer("")
    setError("")
  }, [resetTrigger])

  const generateCaptcha = async () => {
    setLoading(true)
    try {
      const response = await fetch("http://localhost:5001/api/auth/captcha")
      if (response.ok) {
        const data = await response.json()
        setCaptchaText(data.captchaText)
        setCaptchaId(data.captchaId)
        setIsVerified(false)
        setAnswer("")
        setError("")
        if (onVerify) {
          onVerify(false, null, null)
        }
      } else {
        setError("Failed to load CAPTCHA. Please refresh.")
      }
    } catch (error) {
      console.error("Error fetching CAPTCHA:", error)
      setError("Failed to load CAPTCHA. Please refresh.")
    }
    setLoading(false)
  }

  const handleAnswerChange = (e) => {
    const value = e.target.value.toUpperCase().trim()
    setAnswer(value)
    setError("")

    // Auto-verify when answer matches
    if (captchaId && value.length === captchaText.length) {
      if (value === captchaText) {
        setIsVerified(true)
        setError("")
        if (onVerify) {
          onVerify(true, captchaId, value)
        }
      } else {
        setIsVerified(false)
        if (onVerify) {
          onVerify(false, null, null)
        }
      }
    } else {
      setIsVerified(false)
      if (onVerify) {
        onVerify(false, null, null)
      }
    }
  }

  if (loading && num1 === 0 && num2 === 0) {
    return (
      <div style={styles.captchaContainer}>
        <div style={styles.loading}>Loading CAPTCHA...</div>
      </div>
    )
  }

  return (
    <div style={styles.captchaContainer}>
      <div style={styles.captchaQuestion}>
        <span style={styles.captchaText}>
          What is {num1} + {num2}?
        </span>
        <button
          type="button"
          onClick={generateCaptcha}
          style={styles.refreshButton}
          title="Generate new CAPTCHA"
          disabled={loading}
        >
          ðŸ”„
        </button>
      </div>
      <input
        type="number"
        value={answer}
        onChange={handleAnswerChange}
        placeholder="Enter answer"
        style={{
          ...styles.input,
          borderColor: isVerified ? "#4caf50" : error ? "#c62828" : "#ddd",
        }}
        disabled={loading}
        required
      />
      {isVerified && (
        <span style={styles.successIcon}>âœ“ Verified</span>
      )}
      {error && (
        <span style={styles.errorText}>{error}</span>
      )}
    </div>
  )
}

const styles = {
  captchaContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "8px",
    marginBottom: "8px",
  },
  captchaQuestion: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    marginBottom: "4px",
  },
  captchaText: {
    fontSize: "16px",
    fontWeight: "600",
    color: "#333",
    padding: "8px 12px",
    backgroundColor: "#f5f5f5",
    borderRadius: "4px",
    border: "1px solid #ddd",
    minWidth: "140px",
    textAlign: "center",
  },
  refreshButton: {
    background: "none",
    border: "1px solid #ddd",
    borderRadius: "4px",
    padding: "8px 12px",
    cursor: "pointer",
    fontSize: "18px",
    transition: "all 0.2s",
  },
  input: {
    padding: "12px",
    border: "1px solid #ddd",
    borderRadius: "4px",
    fontSize: "16px",
    outline: "none",
    width: "100%",
  },
  successIcon: {
    color: "#4caf50",
    fontSize: "14px",
    fontWeight: "600",
    marginTop: "-4px",
  },
  errorText: {
    color: "#c62828",
    fontSize: "12px",
    marginTop: "-4px",
  },
  loading: {
    padding: "12px",
    textAlign: "center",
    color: "#666",
    fontSize: "14px",
  },
}
