"use client"

import { useState, useEffect, useMemo } from "react"

export default function Captcha({ onVerify, resetTrigger = 0 }) {
  const [captchaText, setCaptchaText] = useState("")
  const [captchaId, setCaptchaId] = useState("")
  const [answer, setAnswer] = useState("")
  const [isVerified, setIsVerified] = useState(false)
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)

  // Generate random rotations and positions for each character
  const characterStyles = useMemo(() => {
    if (!captchaText) return []
    return captchaText.split('').map(() => ({
      rotation: (Math.random() - 0.5) * 16, // -8 to +8 degrees
      yOffset: (Math.random() - 0.5) * 4, // Slight vertical offset for wave effect
    }))
  }, [captchaText])

  useEffect(() => {
    generateCaptcha()
    setIsVerified(false)
    setAnswer("")
    setError("")
  }, [resetTrigger])

  const generateCaptcha = async () => {
    setLoading(true)
    try {
      const response = await fetch("http://localhost:5001/api/auth/captcha")
      if (response.ok) {
        const data = await response.json()
        setCaptchaText(data.captchaText)
        setCaptchaId(data.captchaId)
        setIsVerified(false)
        setAnswer("")
        setError("")
        if (onVerify) {
          onVerify(false, null, null)
        }
      } else {
        setError("Failed to load CAPTCHA. Please refresh.")
      }
    } catch (error) {
      console.error("Error fetching CAPTCHA:", error)
      setError("Failed to load CAPTCHA. Please refresh.")
    }
    setLoading(false)
  }

  const handleAnswerChange = (e) => {
    const value = e.target.value.toUpperCase().trim()
    setAnswer(value)
    setError("")

    // Auto-verify when answer matches
    if (captchaId && value.length === captchaText.length) {
      if (value === captchaText) {
        setIsVerified(true)
        setError("")
        if (onVerify) {
          onVerify(true, captchaId, value)
        }
      } else {
        setIsVerified(false)
        if (onVerify) {
          onVerify(false, null, null)
        }
      }
    } else {
      setIsVerified(false)
      if (onVerify) {
        onVerify(false, null, null)
      }
    }
  }

  if (loading && !captchaText) {
    return (
      <div style={styles.captchaContainer}>
        <div style={styles.loading}>Loading CAPTCHA...</div>
      </div>
    )
  }

  return (
    <div style={styles.captchaContainer}>
      <div style={styles.captchaQuestion}>
        <div style={styles.captchaDisplay}>
          <div style={styles.captchaWrapper}>
            {/* Diagonal lines overlay */}
            <svg style={styles.diagonalLines} width="100%" height="100%" preserveAspectRatio="none">
              <line 
                x1="10%" 
                y1="20%" 
                x2="90%" 
                y2="80%" 
                stroke="rgba(0,0,0,0.15)" 
                strokeWidth="1"
              />
              <line 
                x1="15%" 
                y1="80%" 
                x2="85%" 
                y2="20%" 
                stroke="rgba(0,0,0,0.12)" 
                strokeWidth="1"
              />
            </svg>
            {/* CAPTCHA text with effects */}
            <span style={styles.captchaText}>
              {captchaText.split('').map((char, index) => {
                const style = characterStyles[index] || { rotation: 0, yOffset: 0 }
                // Create wave effect using sine function
                const waveOffset = Math.sin(index * 0.8) * 2
                return (
                  <span
                    key={index}
                    style={{
                      display: 'inline-block',
                      transform: `rotate(${style.rotation}deg) translateY(${style.yOffset + waveOffset}px)`,
                      margin: '0 2px',
                      position: 'relative',
                    }}
                  >
                    {char}
                  </span>
                )
              })}
            </span>
          </div>
        </div>
        <button
          type="button"
          onClick={generateCaptcha}
          style={styles.refreshButton}
          title="Generate new CAPTCHA"
          disabled={loading}
        >
          ðŸ”„
        </button>
      </div>
      <div style={styles.inputWrapper}>
        <input
          type="text"
          value={answer}
          onChange={handleAnswerChange}
          placeholder=""
          maxLength={captchaText.length}
          style={{
            ...styles.input,
            borderColor: isVerified ? "#4caf50" : error ? "#c62828" : "#ddd",
            textTransform: "uppercase",
            letterSpacing: "3px",
            fontSize: "16px",
            fontWeight: "600",
            textAlign: "center",
          }}
          disabled={loading}
          required
        />
        {isVerified && (
          <span style={styles.successIcon}>âœ“ Verified</span>
        )}
      </div>
      {error && (
        <span style={styles.errorText}>{error}</span>
      )}
      <p style={styles.hint}>Enter the letters shown above (case-insensitive)</p>
    </div>
  )
}

const styles = {
  captchaContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
    marginBottom: "8px",
  },
  captchaQuestion: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    marginBottom: "4px",
  },
  captchaDisplay: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  captchaWrapper: {
    position: "relative",
    backgroundColor: "#FFFFFF",
    borderRadius: "8px",
    border: "2px solid #d1d5db",
    padding: "16px 28px",
    minWidth: "200px",
    boxShadow: "0 2px 12px rgba(0,0,0,0.08), inset 0 1px 2px rgba(0,0,0,0.05)",
    overflow: "hidden",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  diagonalLines: {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    pointerEvents: "none",
    zIndex: 1,
  },
  captchaText: {
    fontSize: "26px",
    fontWeight: "600",
    color: "#111827",
    letterSpacing: "6px",
    fontFamily: "Arial, Helvetica, sans-serif",
    textAlign: "center",
    userSelect: "none",
    display: "inline-block",
    lineHeight: "1.4",
    position: "relative",
    zIndex: 2,
    // Add subtle text shadow for depth
    textShadow: "0 1px 2px rgba(0,0,0,0.1)",
  },
  refreshButton: {
    background: "white",
    border: "1px solid #ddd",
    borderRadius: "6px",
    padding: "10px 14px",
    cursor: "pointer",
    fontSize: "20px",
    transition: "all 0.2s",
  },
  inputWrapper: {
    position: "relative",
    width: "100%",
  },
  input: {
    padding: "12px",
    border: "2px solid #ddd",
    borderRadius: "4px",
    fontSize: "16px",
    outline: "none",
    width: "100%",
    boxSizing: "border-box",
  },
  successIcon: {
    position: "absolute",
    right: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#4caf50",
    fontSize: "18px",
    fontWeight: "600",
  },
  errorText: {
    color: "#c62828",
    fontSize: "12px",
    marginTop: "-8px",
  },
  loading: {
    padding: "12px",
    textAlign: "center",
    color: "#666",
    fontSize: "14px",
  },
  hint: {
    fontSize: "12px",
    color: "#666",
    margin: "0",
    fontStyle: "italic",
  },
}
