"use client"

import { useState, useEffect } from "react"

export default function Captcha({ onVerify, resetTrigger = 0 }) {
  const [num1, setNum1] = useState(0)
  const [num2, setNum2] = useState(0)
  const [answer, setAnswer] = useState("")
  const [isVerified, setIsVerified] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    generateCaptcha()
    setIsVerified(false)
    setAnswer("")
    setError("")
  }, [resetTrigger])

  const generateCaptcha = () => {
    const n1 = Math.floor(Math.random() * 10) + 1
    const n2 = Math.floor(Math.random() * 10) + 1
    setNum1(n1)
    setNum2(n2)
    setIsVerified(false)
    setAnswer("")
    setError("")
    if (onVerify) {
      onVerify(false, null)
    }
  }

  const handleAnswerChange = (e) => {
    const value = e.target.value
    setAnswer(value)
    setError("")

    // Auto-verify when answer is entered
    const userAnswer = parseInt(value)
    if (!isNaN(userAnswer)) {
      const correctAnswer = num1 + num2
      if (userAnswer === correctAnswer) {
        setIsVerified(true)
        setError("")
        if (onVerify) {
          onVerify(true, correctAnswer)
        }
      } else if (value.length > 0) {
        setIsVerified(false)
        if (onVerify) {
          onVerify(false, null)
        }
      }
    } else {
      setIsVerified(false)
      if (onVerify) {
        onVerify(false, null)
      }
    }
  }

  return (
    <div style={styles.captchaContainer}>
      <div style={styles.captchaQuestion}>
        <span style={styles.captchaText}>
          What is {num1} + {num2}?
        </span>
        <button
          type="button"
          onClick={generateCaptcha}
          style={styles.refreshButton}
          title="Generate new CAPTCHA"
        >
          ðŸ”„
        </button>
      </div>
      <input
        type="number"
        value={answer}
        onChange={handleAnswerChange}
        placeholder="Enter answer"
        style={{
          ...styles.input,
          borderColor: isVerified ? "#4caf50" : error ? "#c62828" : "#ddd",
        }}
        required
      />
      {isVerified && (
        <span style={styles.successIcon}>âœ“ Verified</span>
      )}
      {error && (
        <span style={styles.errorText}>{error}</span>
      )}
    </div>
  )
}

const styles = {
  captchaContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "8px",
    marginBottom: "8px",
  },
  captchaQuestion: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    marginBottom: "4px",
  },
  captchaText: {
    fontSize: "16px",
    fontWeight: "600",
    color: "#333",
    padding: "8px 12px",
    backgroundColor: "#f5f5f5",
    borderRadius: "4px",
    border: "1px solid #ddd",
    minWidth: "140px",
    textAlign: "center",
  },
  refreshButton: {
    background: "none",
    border: "1px solid #ddd",
    borderRadius: "4px",
    padding: "8px 12px",
    cursor: "pointer",
    fontSize: "18px",
    transition: "all 0.2s",
  },
  input: {
    padding: "12px",
    border: "1px solid #ddd",
    borderRadius: "4px",
    fontSize: "16px",
    outline: "none",
    width: "100%",
  },
  successIcon: {
    color: "#4caf50",
    fontSize: "14px",
    fontWeight: "600",
    marginTop: "-4px",
  },
  errorText: {
    color: "#c62828",
    fontSize: "12px",
    marginTop: "-4px",
  },
}
