"use client"

import { useState, useContext } from "react"
import { useNavigate } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import { Mail, Lock, Loader2, User } from "lucide-react"

export default function LoginPage() {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [name, setName] = useState("")
  const [role, setRole] = useState("student")
  const [errors, setErrors] = useState({})
  const [touched, setTouched] = useState({})
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState({ type: "", text: "" })
  const [isLogin, setIsLogin] = useState(true)

  const { login } = useContext(AuthContext)
  const navigate = useNavigate()

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (!email) return "Email is required"
    if (!emailRegex.test(email)) return "Invalid email format"
    return ""
  }

  const validatePassword = (password) => {
    if (!password) return "Password is required"
    if (password.length < 6) return "Password must be at least 6 characters"
    return ""
  }

  const validateName = (name) => {
    if (!name) return "Name is required"
    if (name.length < 2) return "Name must be at least 2 characters"
    return ""
  }

  const handleBlur = (field) => {
    setTouched({ ...touched, [field]: true })
    if (field === "email") {
      setErrors({ ...errors, email: validateEmail(email) })
    } else if (field === "password") {
      setErrors({ ...errors, password: validatePassword(password) })
    } else if (field === "name") {
      setErrors({ ...errors, name: validateName(name) })
    }
  }

  const handleEmailChange = (e) => {
    const value = e.target.value
    setEmail(value)
    if (touched.email) {
      setErrors({ ...errors, email: validateEmail(value) })
    }
  }

  const handlePasswordChange = (e) => {
    const value = e.target.value
    setPassword(value)
    if (touched.password) {
      setErrors({ ...errors, password: validatePassword(value) })
    }
  }

  const handleNameChange = (e) => {
    const value = e.target.value
    setName(value)
    if (touched.name) {
      setErrors({ ...errors, name: validateName(value) })
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    const emailError = validateEmail(email)
    const passwordError = validatePassword(password)
    const nameError = !isLogin ? validateName(name) : ""

    setTouched({ email: true, password: true, name: !isLogin })
    setErrors({ email: emailError, password: passwordError, name: nameError })

    if (emailError || passwordError || nameError) {
      setMessage({ type: "error", text: "Please fix the errors above" })
      return
    }

    setIsLoading(true)
    setMessage({ type: "", text: "" })

    try {
      const endpoint = isLogin ? "/api/auth/login" : "/api/auth/register"
      const payload = isLogin 
        ? { email, password } 
        : { name, email, password, role }

      const response = await fetch(`http://localhost:5001${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      })

      const data = await response.json()

      if (!response.ok) {
        setMessage({ type: "error", text: data.message || "Failed" })
        setIsLoading(false)
        return
      }

      login(data.user, data.token)
      setIsLoading(false)
      setMessage({ type: "success", text: "Success! Redirecting..." })

      setTimeout(() => {
        if (data.user.role === "admin") {
          navigate("/admin")
        } else {
          navigate("/student")
        }
      }, 500)
    } catch (err) {
      setMessage({ type: "error", text: "Network error. Please try again." })
      setIsLoading(false)
    }
  }

  const toggleMode = () => {
    setIsLogin(!isLogin)
    setMessage({ type: "", text: "" })
    setErrors({})
    setTouched({})
    setEmail("")
    setPassword("")
    setName("")
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-600 via-purple-500 to-pink-500 p-4 relative overflow-hidden">
      {/* Decorative Background */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-32 h-32 bg-white/10 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 right-10 w-40 h-40 bg-pink-300/20 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/4 w-24 h-24 bg-purple-300/10 rounded-full blur-2xl"></div>

        <svg className="absolute bottom-0 left-0 w-full" viewBox="0 0 1440 320" fill="none">
          <path fill="rgba(255,255,255,0.05)" d="M0,96L48,112C96,128,192,160,288,160C384,160,480,128,576,112C672,96,768,96,864,112C960,128,1056,160,1152,160C1248,160,1344,128,1392,112L1440,96L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
        </svg>
      </div>

      <div className="max-w-6xl w-full bg-white rounded-3xl shadow-2xl overflow-hidden flex relative z-10">
        {/* Left Side - Illustration */}
        <div className="hidden lg:flex lg:w-1/2 bg-gradient-to-br from-purple-100 to-pink-50 p-12 items-center justify-center relative">
          <div className="relative w-full max-w-md">
            <div className="relative">
              {/* Student card */}
              <div className="bg-purple-600 rounded-3xl p-8 shadow-xl relative z-10">
                <div className="flex items-center space-x-4 mb-6">
                  <div className="w-16 h-16 bg-purple-400 rounded-full flex items-center justify-center">
                    <div className="w-12 h-12 bg-white rounded-full"></div>
                  </div>
                  <div className="flex-1">
                    <div className="h-3 bg-purple-300 rounded-full mb-2 w-3/4"></div>
                    <div className="h-2 bg-purple-400 rounded-full w-1/2"></div>
                  </div>
                </div>
                <div className="bg-purple-500 rounded-2xl p-4 space-y-3">
                  <div className="h-2 bg-purple-400 rounded-full"></div>
                  <div className="h-2 bg-purple-400 rounded-full w-5/6"></div>
                  <div className="h-2 bg-purple-400 rounded-full w-4/6"></div>
                </div>
              </div>
              {/* Floating books */}
              <div className="absolute -top-6 -right-6 w-20 h-24 bg-pink-400 rounded-lg transform rotate-12 shadow-lg"></div>
              <div className="absolute -top-2 -right-10 w-20 h-24 bg-yellow-400 rounded-lg transform rotate-6 shadow-lg"></div>
              <div className="absolute top-4 -right-14 w-20 h-24 bg-blue-400 rounded-lg transform -rotate-3 shadow-lg"></div>
              {/* Clock decoration */}
              <div className="absolute -bottom-8 -left-8 w-16 h-16 bg-pink-400 rounded-full shadow-lg flex items-center justify-center">
                <div className="w-8 h-8 border-4 border-white rounded-full"></div>
              </div>

              {/* Stars */}
              <div className="absolute top-0 left-0 text-yellow-400 text-2xl">★</div>
              <div className="absolute bottom-10 right-0 text-pink-400 text-xl">★</div>
              <div className="absolute top-20 -left-6 text-purple-400 text-lg">★</div>
            </div>
            {/* Info card */}
            <div className="absolute -bottom-4 left-12 bg-white rounded-2xl p-4 shadow-xl">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-green-400 rounded-full"></div>
                <div>
                  <div className="h-2 bg-gray-300 rounded-full w-20 mb-2"></div>
                  <div className="h-2 bg-gray-200 rounded-full w-16"></div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="w-full lg:w-1/2 p-12 flex flex-col justify-center">
          <div className="max-w-md mx-auto w-full">
            <div className="text-center mb-8">
              <h1 className="text-4xl font-bold text-gray-800 mb-2">
                {isLogin ? "Welcome Back!" : "Join Us!"}
              </h1>
              <p className="text-gray-600">
                {isLogin 
                  ? "Sign in to continue your learning journey" 
                  : "Create your account to get started"}
              </p>
            </div>

            <div className="space-y-6">
              {/* Name Field (Sign Up only) */}
              {!isLogin && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Full Name
                  </label>
                  <div className="relative">
                    <User className={`absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 ${
                      errors.name && touched.name ? "text-red-500" : "text-gray-400"
                    }`} />
                    <input
                      type="text"
                      value={name}
                      onChange={handleNameChange}
                      onBlur={() => handleBlur("name")}
                      placeholder="John Doe"
                      className={`w-full pl-12 pr-4 py-3 border-2 rounded-xl focus:outline-none transition-colors ${
                        errors.name && touched.name
                          ? "border-red-500 focus:border-red-600"
                          : !errors.name && touched.name && name
                          ? "border-green-500 focus:border-green-600"
                          : "border-gray-300 focus:border-purple-500"
                      }`}
                    />
                  </div>
                  {errors.name && touched.name && (
                    <p className="mt-1 text-sm text-red-600">{errors.name}</p>
                  )}
                </div>
              )}

              {/* Email Field */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Email Address
                </label>
                <div className="relative">
                  <Mail className={`absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 ${
                    errors.email && touched.email ? "text-red-500" : "text-gray-400"
                  }`} />
                  <input
                    type="email"
                    value={email}
                    onChange={handleEmailChange}
                    onBlur={() => handleBlur("email")}
                    placeholder="student@example.com"
                    className={`w-full pl-12 pr-4 py-3 border-2 rounded-xl focus:outline-none transition-colors ${
                      errors.email && touched.email
                        ? "border-red-500 focus:border-red-600"
                        : !errors.email && touched.email && email
                        ? "border-green-500 focus:border-green-600"
                        : "border-gray-300 focus:border-purple-500"
                    }`}
                  />
                </div>
                {errors.email && touched.email && (
                  <p className="mt-1 text-sm text-red-600">{errors.email}</p>
                )}
              </div>

              {/* Password Field */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Password
                </label>
                <div className="relative">
                  <Lock className={`absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 ${
                    errors.password && touched.password ? "text-red-500" : "text-gray-400"
                  }`} />
                  <input
                    type="password"
                    value={password}
                    onChange={handlePasswordChange}
                    onBlur={() => handleBlur("password")}
                    placeholder="••••••••"
                    className={`w-full pl-12 pr-4 py-3 border-2 rounded-xl focus:outline-none transition-colors ${
                      errors.password && touched.password
                        ? "border-red-500 focus:border-red-600"
                        : !errors.password && touched.password && password
                        ? "border-green-500 focus:border-green-600"
                        : "border-gray-300 focus:border-purple-500"
                    }`}
                  />
                </div>
                {errors.password && touched.password && (
                  <p className="mt-1 text-sm text-red-600">{errors.password}</p>
                )}
              </div>

              {/* Role Selection (Sign Up only) */}
              {!isLogin && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Select Your Role
                  </label>
                  <select
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    className="w-full pl-4 pr-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:border-purple-500 transition-colors"
                  >
                    <option value="student">Student</option>
                    <option value="admin">Administrator</option>
                  </select>
                </div>
              )}

              {/* Remember & Forgot (Login only) */}
              {isLogin && (
                <div className="flex items-center justify-between text-sm">
                  <label className="flex items-center cursor-pointer">
                    <input type="checkbox" className="mr-2 rounded" />
                    <span className="text-gray-600">Remember me</span>
                  </label>
                  <button className="text-purple-600 hover:text-purple-700 font-medium">
                    Forgot password?
                  </button>
                </div>
              )}

              {/* Message */}
              {message.text && (
                <div className={`p-4 rounded-xl ${
                  message.type === "success" 
                    ? "bg-green-50 text-green-800 border border-green-200" 
                    : "bg-red-50 text-red-800 border border-red-200"
                }`}>
                  {message.text}
                </div>
              )}

              {/* Submit Button */}
              <button
                onClick={handleSubmit}
                disabled={isLoading}
                className="w-full bg-gradient-to-r from-purple-600 to-pink-600 text-white py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 transition-all disabled:opacity-70 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
              >
                {isLoading ? (
                  <>
                    <Loader2 className="w-5 h-5 animate-spin" />
                    <span>{isLogin ? "Signing in..." : "Creating account..."}</span>
                  </>
                ) : (
                  <span>{isLogin ? "Sign In" : "Sign Up"}</span>
                )}
              </button>

              {/* Social Login (Login only) */}
              {isLogin && (
                <>
                  <div className="relative">
                    <div className="absolute inset-0 flex items-center">
                      <div className="w-full border-t border-gray-300"></div>
                    </div>
                    <div className="relative flex justify-center text-sm">
                      <span className="px-4 bg-white text-gray-500">Or continue with</span>
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-3">
                    <button className="flex items-center justify-center px-4 py-2 border-2 border-gray-300 rounded-lg hover:border-purple-500 transition-colors">
                      <span className="font-bold text-red-600">G</span>
                    </button>
                    <button className="flex items-center justify-center px-4 py-2 border-2 border-gray-300 rounded-lg hover:border-purple-500 transition-colors">
                      <span className="font-bold text-blue-600">f</span>
                    </button>
                    <button className="flex items-center justify-center px-4 py-2 border-2 border-gray-300 rounded-lg hover:border-purple-500 transition-colors">
                      <span className="font-bold text-blue-500">in</span>
                    </button>
                  </div>
                </>
              )}

              {/* Toggle Link */}
              <div className="text-center text-sm">
                <span className="text-gray-600">
                  {isLogin ? "Don't have an account? " : "Already have an account? "}
                </span>
                <button 
                  onClick={toggleMode}
                  className="text-purple-600 hover:text-purple-700 font-semibold"
                >
                  {isLogin ? "Sign Up Now" : "Sign In"}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
